mapper.select:
select * from attachment where attm_status='Y'
and attm_ref_type = 'B' and attm_ref_no = #{ bNo }
order by attm_id

mapper.update:
update board
set b_count = b_count +1
where b_no = #{bNo}

update board
set b_title = #{bTitle}, b_content = #{bContent}
where b_no = #{ bNo }

update board
set b_status = 'N'
where b_no = #{ bNo }

update attachment
set attm_status = 'N'
where attm_ref_no = #{attmRefNo}

update Reply
set re_d_status = 'N'
where r_no = #{ rNo }
