mapper.select:
select * from attachment where attm_status='Y'
and attm_ref_type = 'B' and attm_ref_no = #{ bNo }
order by attm_id

mapper.update:
update board
set b_count = b_count +1
where b_no = #{bNo}

update board
set b_title = #{bTitle}, b_content = #{bContent}
where b_no = #{ bNo }

update board
set b_status = 'N'
where b_no = #{ bNo }

update attachment
set attm_status = 'N'
where attm_ref_no = #{attmRefNo}

mapper.select:
select * from attachment where attm_status='Y'
and attm_ref_type = 'DW' and attm_ref_no = #{ dwNo }
order by attm_id

select * from attachment where attm_status='Y'
and attm_ref_type = 'DW' and attm_ref_no = #{ dwNo }
order by attm_id

mapper.update:
update dw_board
set dw_count = dw_count +1
where dw_no = #{dwNo}

update dw_board
set dw_title = #{dwTitle}, dw_type = #{dwType}, dw_content = #{dwContent}, dw_price=#{dwPrice}, dw_location =#{dwLocation}
where dw_no = #{ dwNo }

update dw_board
set dw_status = 'N'
where dw_no = #{ dwNo }

update reply
set re_content = #{reContent}
where r_No = #{rNo}

mapper.select:
select count(*)
from report
where r_accuser= #{rAccuser} and r_board_no = #{rBoardNo}

