mapper.select:
select count(*)
from used_board
where buyer_id = #{id}

		select *
		from used_board
			 join attachment on (u_no = attm_ref_no)
 		where buyer_id = #{id} and attm_ref_type = 'U' and attm_level = 1
 			
		    	and sold_date >= #{startDate}
		    
		    
		    	and sold_date < #{endDate}
		    
			
			    order by sold_date ${orderDate}
			
			
			    order by product_price ${price}

select count(*)
from used_board
where mb_id = #{id}

		select *
		from used_board
			 join attachment on (u_no = attm_ref_no)
 		where mb_id = #{id} and attm_ref_type = 'U' and attm_level = 1
 			
		    	and sold_date >= #{startDate}
		    
		    
		    	and sold_date < #{endDate}
		    
	        
		    	or (mb_id = #{id} and sold_date is null)
		    
			
			    order by sold_date ${soldDate}
			
			
			    order by product_price ${price}
			
			
			    order by u_end ${uEnd}

select * from attachment where attm_status='Y'
and attm_ref_type = 'B' and attm_ref_no = #{ bNo }
order by attm_id

mapper.update:
update board
set b_count = b_count +1
where b_no = #{bNo}

update board
set b_title = #{bTitle}, b_content = #{bContent}
where b_no = #{ bNo }

update board
set b_status = 'N'
where b_no = #{ bNo }

update attachment
set attm_status = 'N'
where attm_ref_no = #{attmRefNo}

