mapper.update:
update dw_board
set dw_count = dw_count +1
where dw_no = #{dwNo}

update dw_board
set dw_title = #{dwTitle}, dw_type = #{dwType}, dw_content = #{dwContent}, dw_price=#{dwPrice}, dw_location =#{dwLocation}
where dw_no = #{ dwNo }

update dw_board
set dw_status = 'N'
where dw_no = #{ dwNo }

update reply
set re_content = #{reContent}
where r_No = #{rNo}

