mapper.select:
select * from ( select ROW_NUMBER() OVER(PARTITION BY attm_Ref_No ORDER BY attm_id ASC) AS RNUM , m.* from attachment m where attm_status='Y' and attm_ref_type = 'M'
and attm_ref_no = #{bId}
) A WHERE 1=1
AND RNUM = 1
order by attm_id DESC

mapper.update:
update missing_board
set m_count = m_count +1
where m_no = #{mNo}

update missing_board
set m_status = 'N'
where m_no = #{bId}

update attachment
set attm_status = 'N'
where attm_ref_type = 'M' and attm_ref_no = #{bId}

update missing_board
set m_title = #{mTitle}, m_content = #{mContent}, M_RESCUE = #{mRescue}
where m_no = #{mNo}

mapper.select:
select * from ( select ROW_NUMBER() OVER(PARTITION BY attm_Ref_No ORDER BY attm_id ASC) AS RNUM , u.* from attachment u where attm_status='Y' and attm_ref_type = 'M'
and attm_ref_no = #{bId}
) A WHERE 1=1
AND RNUM = 1
order by attm_id DESC

