mapper.select:
select count(*)
from used_board
where buyer_id = #{id}

		select *
		from used_board
 		where buyer_id = #{id}
 			
		    	and sold_date >= #{startDate}
		    
		    
		    	and sold_date < #{endDate}
		    
			
			    order by sold_date ${orderDate}
			
			
			    order by product_price ${price}

select count(*)
from used_board
where mb_id = #{id}

		select *
		from used_board
 		where mb_id = #{id}
 			
		    	and sold_date >= #{startDate}
		    
		    
		    	and sold_date < #{endDate}
		    
			
			    order by sold_date ${soldDate}
			
			
			    order by product_price ${price}
			
			
			    order by u_end ${uEnd}

mapper.update:
update board
set b_count = b_count +1
where b_no = #{bNo}

update board
set b_status = 'N'
where b_no = #{ bNo }

update Reply
set re_d_status = 'N'
where r_no = #{ rNo }

update attachment
set attm_status = 'N'
where attm_ref_no = #{attmRefNo}
