mapper.update:
UPDATE contest
SET CON_GEN = #{conGen},  CON_NAME = #{conName}, CON_START_DATE = #{conStartDate}, CON_END_DATE = #{conEndDate}
WHERE CON_NO = #{conNo}

UPDATE contest
SET CON_STATUS = 'N'

where CON_END_DATE < #{today}

mapper.select:
select *
from contest

where CON_START_DATE < #{today} AND CON_END_DATE > #{today}

select *
from contest

where CON_START_DATE < #{today} AND CON_END_DATE > #{today}
