mapper.update:
update pet
set pet_name = #{newPetName}
where pet_id = #{petId}

update pet
set pet_intro = #{newPetIntro}
where pet_id = #{petId}

update pet
set pet_birth = #{newPetBirth}
where pet_id = #{petId}

update pet
set pet_breed = #{newPetBreed}
where pet_id = #{petId}

update pet
set pet_weight = #{newPetWeight}
where pet_id = #{petId}

update pet
set pet_gender = #{newPetGender}
where pet_id = #{petId}

update pet
set pet_memo = #{newPetMemo}
where pet_id = #{petId}

update pet
set pet_status = 'N'
where pet_id = #{petId}

update pet
set pet_profile= #{petProfile}
where pet_id = #{petId}

update pet
set pet_profile = default
where pet_id = #{petId}

update diary
set dr_content = #{drContent} , dr_date = #{drDate}, pet_id = #{petId}, pet_health = #{petHealth}
where dr_no = #{drNo}

update diary
set dr_status = 'N'
where dr_no = #{drNo}

mapper.select:
select *
from album
where ab_status = 'Y' and writer_id = #{id}

mapper.update:
update album
set ab_status = 'N'
where ab_no = #{abNo}
